import{_ as e,o as t,c as o,Q as d}from"./app.c8042f0a.js";const f='{"title":"DropTargetMonitor","description":"","frontmatter":{},"headers":[{"level":2,"title":"method","slug":"method"}],"relativePath":"en/guide/monitors/drop-target-monitor.md"}',r={},n=d('<h1 id="droptargetmonitor" tabindex="-1">DropTargetMonitor <a class="header-anchor" href="#droptargetmonitor" aria-hidden="true">#</a></h1><p><code>DropTargetMonitor</code> is the object passed to <code>DropTarget</code> drop target. It provides methods that allow you to get some status information about the drop target. The specific drop target bound to this monitor is referred to below as the monitor&#39;s owner.</p><h2 id="method" tabindex="-1">method <a class="header-anchor" href="#method" aria-hidden="true">#</a></h2><table><thead><tr><th>method name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td><code>canDrop()</code></td><td><code>() =&gt; boolean</code></td><td><code>true</code> if there is a drag operation in progress and the <code>canDrop()</code> method of the owner <code>useDrop</code> returns <code>true</code> or <code>undefined</code> <code>, otherwise return </code>false`.</td></tr><tr><td><code>isOver()</code></td><td><code>(options?: { shallow: boolean }) =&gt; boolean</code></td><td>Returns true if there is a drag operation in progress and the pointer is currently hovering over the owner. You can optionally pass <code>{shallow: true}</code> to strictly check that only the owner is suspended, not nested targets.</td></tr><tr><td><code>getItemType()</code></td><td><code>() =&gt; (string | null)</code></td><td>Returns the type <code>type</code> that identifies the currently dragged item. Returns <code>null</code> if no item was dragged.</td></tr><tr><td><code>getItem()</code></td><td><code>() =&gt; (object | null)</code></td><td>Returns a plain object representing the currently dragged item. Each drag source must be specified by specifying it from its <code>item</code> property. Returns <code>null</code> if no item was dragged.</td></tr><tr><td><code>getDropResult()</code></td><td><code>() =&gt; (object | null)</code></td><td>Returns a plain object representing the drop result of the last record. <br>Drop targets can be specified by returning an object from their <code>drop()</code> method. <br>When the drop target is a nested target, any parent object that explicitly returns its own result from <code>drop()</code> overrides the <code>drop</code> result previously set by the child object. <br><strong>It can only be called inside <code>drop()</code></strong>, if called outside <code>drop()</code> it returns <code>null</code>.</td></tr><tr><td><code>didDrop()</code></td><td><code>() =&gt; boolean</code></td><td>Returns <code>true</code> if a drop target has handled the drop event, <code>false</code> otherwise. <code>didDrop()</code> returns <code>true</code> even if the target does not return a drop result. <br>It can be used in <code>drop()</code> to check if a drop target has already handled the drag. Returns <code>false</code> if called outside <code>drop()</code>.</td></tr><tr><td><code>getInitialClientOffset()</code></td><td><code>() =&gt; { x: number, y: number }</code></td><td>Returns the initial position <code>{x, y}</code> of the mouse pointer relative to the client at the start of the current drag. <br>Returns null if no item was dragged.</td></tr><tr><td><code>getInitialSourceClientOffset()</code></td><td><code>() =&gt; { x: number, y: number }</code></td><td>Returns the initial position of the root DOM node of the drag source relative to the client when the current drag starts <code>{x, y}</code> . <br>Returns null if no item was dragged.</td></tr><tr><td><code>getClientOffset()</code></td><td><code>() =&gt; { x: number, y: number }</code></td><td>Returns the offset of the mouse pointer relative to the client in the current drag <code>{x, y}</code>. <br>Returns null if no item was dragged.</td></tr><tr><td><code>getDifferenceFromInitialOffset()</code></td><td><code>() =&gt; { x: number, y: number }</code></td><td>Returns the offset of the mouse pointer in the current drag relative to the mouse pointer position when the drag started <code>{x, y} </code>. <br>Returns null if no item was dragged.</td></tr><tr><td><code>getSourceClientOffset()</code></td><td><code>() =&gt; { x: number, y: number }</code></td><td>Returns the offset <code>{x, y}</code> of the root DOM node of the current drag source relative to the client. <br>Returns null if no item was dragged.</td></tr></tbody></table>',4),c=[n];function a(i,s,h,l,u,g){return t(),o("div",null,c)}var m=e(r,[["render",a]]);export{f as __pageData,m as default};
